mapper.select:
select d.id id, d.name, category_id, price, image, description, d.status, d.update_time, c.name category_name from dish d left join category c on d.category_id = c.id
and category_id = #{categoryId}
and d.name like concat('%', #{name}, '%')
and d.status = #{status}
order by price

mapper.update:
update dish


        , name = #{name}


        , category_id = #{categoryId}


        , price = #{price}


        , image = #{image}


        , description = #{description}


        , status = #{status}


        , update_time = #{updateTime}


        , update_user = #{updateUser}


where id = #{id}

mapper.select:
select * from dish where status = 1

    and id = #{id}


    and name = #{name}


    and category_id = #{categoryId}

select count(*) from dish
where status = 1 and id in

    #{id}
