mapper.select:
select s.id, s.category_id, s.name, s.price, s.status, s.description, s.image, s.update_time, c.name categoryName from set_meal s left join category c on s.category_id = c.id
and s.categoryId = #{categoryId}
and s.name like concat('%', #{name}, '%')
and s.status = #{status}
order by price
